mapper.delete:
UPDATE FREEBOARD
SET DELETED = 1
WHERE article_no= #{articleNo}

mapper.update:
UPDATE FREEBOARD
SET 
article_title = #{articleTitle},
article_content = #{articleContent}
WHERE ARTICLE_NO = #{articleNo}

UPDATE FREEBOARD
SET read_count = read_count + 1 
WHERE article_no = #{articleNo};

mapper.select:
SELECT *
FROM FREEBOARD
WHERE DELETED=0 and id=#{id}
ORDER BY article_no DESC 
limit #{start}, #{count}
